<?phpclass Contrulor{    private $registy;	private $action;	private $method;	//private $arags;		public function __construct($registy){	    $this->registy = $registy;	    $this->action = isset($_GET['action']) ? 'index' : $_GET['action'];		$this->method = isset($_GET['method']) ? 'index' : $_GET['method'];				$this->loadAction();	}		private function loadAction(){	    if(file_exists(APP_MODULE . $this->action . 'Module.php')){
	        require_once (APP_MODULE . $this->action . 'Module.php');
	    }	    		if(file_exists(APP_ACTION . $this->action . 'Action.php')){			require_once (APP_ACTION . $this->action . 'Action.php');			$actionClass =  $this->action . 'Action';			$action = new $actionClass ($this->registy);			if(method_exists($action, $this->method)) {				call_user_func_array(array($action, $this->method), array());			} else {				exit('错误: Method ' . $this->method . '不存在！');			}		} else {			exit('错误: Action ' . $this->action . '不存在！');		}			}}?>